import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', course: '' });
  const [editId, setEditId] = useState(null);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/students', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStudents(res.data);
    } catch (err) {
      setError('Failed to fetch students');
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setError('');
    try {
      if (editId) {
        await axios.put(`http://localhost:5000/api/students/${editId}`, form, {
          headers: { Authorization: `Bearer ${token}` }
        });
      } else {
        await axios.post('http://localhost:5000/api/students', form, {
          headers: { Authorization: `Bearer ${token}` }
        });
      }
      setForm({ name: '', email: '', course: '' });
      setEditId(null);
      fetchStudents();
    } catch (err) {
      setError('Failed to save student');
    }
  };

  const onEdit = student => {
    setEditId(student._id);
    setForm({
      name: student.user.name,
      email: student.user.email,
      course: student.course,
    });
  };

  const onDelete = async id => {
    setError('');
    try {
      await axios.delete(`http://localhost:5000/api/students/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchStudents();
    } catch (err) {
      setError('Failed to delete student');
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    window.location.href = '/login';
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      {error && <p style={{color:'red', textAlign:'center'}}>{error}</p>}

      <form onSubmit={onSubmit} style={{ marginBottom: '30px' }}>
        <input
          type="text"
          name="name"
          placeholder="Student Name"
          value={form.name}
          onChange={onChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Student Email"
          value={form.email}
          onChange={onChange}
          required
        />
        <input
          type="text"
          name="course"
          placeholder="Course"
          value={form.course}
          onChange={onChange}
          required
        />
        <button type="submit">{editId ? 'Update Student' : 'Add Student'}</button>
      </form>

      <table className="student-list">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Course</th>
            <th>Enrollment Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.length === 0 && (
            <tr>
              <td colSpan="5" style={{ textAlign: 'center' }}>No students found</td>
            </tr>
          )}
          {students.map(student => (
            <tr key={student._id}>
              <td>{student.user.name}</td>
              <td>{student.user.email}</td>
              <td>{student.course}</td>
              <td>{new Date(student.enrollmentDate).toLocaleDateString()}</td>
              <td className="action-buttons">
                <button className="edit-btn" onClick={() => onEdit(student)}>Edit</button>
                <button className="delete-btn" onClick={() => onDelete(student._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={logout} className="logout-btn" style={{ marginTop: '30px' }}>
        Logout
      </button>
    </div>
  );
};

export default AdminDashboard;
