import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const StudentDashboard = () => {
  const [student, setStudent] = useState(null);
  const [form, setForm] = useState({ name: '', email: '', course: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const fetchProfile = async token => {
    try {
      const res = await axios.get('http://localhost:5000/api/students/me', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = res.data;
      setStudent(data.user || {});
      setForm({
        name: data.user?.name || '',
        email: data.user?.email || '',
        course: data.course || '',
      });
    } catch (err) {
      localStorage.removeItem('token');
      localStorage.removeItem('role');
      navigate('/login');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');
    fetchProfile(token);
  }, [navigate]);

  const onChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setError('');
    setSuccess('');
  };

  const onSubmit = async e => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');
    try {
      await axios.put('http://localhost:5000/api/students/me', form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSuccess('Profile updated successfully');
      fetchProfile(token);
    } catch (err) {
      setError('Failed to update profile');
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/login');
  };

  if (loading) return <p>Loading profile...</p>;
  if (!student) return <p>Unable to load profile. Please login again.</p>;

  return (
    <div className="student-dashboard">
      <h2>Student Dashboard</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <div className="profile-info">
        <p><strong>Name:</strong> {student?.name || 'N/A'}</p>
        <p><strong>Email:</strong> {student?.email || 'N/A'}</p>
        <p><strong>Course:</strong> {student?.course || 'N/A'}</p>
      </div>

      <form onSubmit={onSubmit}>
        <input type="text" name="name" value={form.name} onChange={onChange} required />
        <input type="email" name="email" value={form.email} onChange={onChange} required />
        <input type="text" name="course" value={form.course} onChange={onChange} />
        <button type="submit">Update Profile</button>
      </form>

      <button onClick={logout} style={{ marginTop: '20px' }}>Logout</button>
    </div>
  );
};

export default StudentDashboard;
