import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentDashboard = () => {
  const [student, setStudent] = useState(null);
  const [form, setForm] = useState({ name: '', email: '', course: '' });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const token = localStorage.getItem('token');

  const fetchProfile = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/students/me', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStudent(res.data);
      setForm({
        name: res.data.user.name,
        email: res.data.user.email,
        course: res.data.course,
      });
    } catch (err) {
      setError('Failed to fetch profile');
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  const onChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setSuccess('');
    setError('');
  };

  const onSubmit = async e => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      await axios.put('http://localhost:5000/api/students/me', form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Profile updated successfully');
      fetchProfile();
    } catch (err) {
      setError('Failed to update profile');
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    window.location.href = '/login';
  };

  if (!student) {
    return (
      <div className="student-dashboard">
        <h2>Student Dashboard</h2>
        <p>Loading profile...</p>
      </div>
    );
  }

  return (
    <div className="student-dashboard">
      <h2>Student Dashboard</h2>
      {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
      {success && <p style={{ color: 'green', textAlign: 'center' }}>{success}</p>}

      <div className="profile-info">
        <p><strong>Name:</strong> {student.user.name}</p>
        <p><strong>Email:</strong> {student.user.email}</p>
        <p><strong>Course:</strong> {student.course}</p>
        <p><strong>Enrollment Date:</strong> {new Date(student.enrollmentDate).toLocaleDateString()}</p>
      </div>

      <form onSubmit={onSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={onChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={onChange}
          required
        />
        <input
          type="text"
          name="course"
          placeholder="Course"
          value={form.course}
          onChange={onChange}
          required
        />
        <button type="submit">Update Profile</button>
      </form>

      <button onClick={logout} className="logout-btn" style={{ marginTop: '30px' }}>
        Logout
      </button>
    </div>
  );
};

export default StudentDashboard;
