// components/AdminDashboard.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './Dashboard.css';

const AdminDashboard = () => {
  const { currentUser, logout } = useAuth();
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    course: 'IT',
    password: 'defaultPassword'
  });
  const [editMode, setEditMode] = useState(false);
  const [currentStudentId, setCurrentStudentId] = useState(null);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('/api/students');
      setStudents(response.data);
      setLoading(false);
    } catch (error) {
      setError('Failed to fetch students');
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      if (editMode) {
        await axios.put(`/api/students/${currentStudentId}`, formData);
        setSuccess('Student updated successfully');
      } else {
        await axios.post('/api/students', formData);
        setSuccess('Student added successfully');
      }
      
      setFormData({
        name: '',
        email: '',
        course: 'IT',
        password: 'defaultPassword'
      });
      setEditMode(false);
      setCurrentStudentId(null);
      fetchStudents();
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to save student');
    }
  };

  const handleEdit = (student) => {
    setFormData({
      name: student.name,
      email: student.email,
      course: student.course,
      password: ''
    });
    setEditMode(true);
    setCurrentStudentId(student._id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        await axios.delete(`/api/students/${id}`);
        setSuccess('Student deleted successfully');
        fetchStudents();
      } catch (error) {
        setError('Failed to delete student');
      }
    }
  };

  const cancelEdit = () => {
    setFormData({
      name: '',
      email: '',
      course: 'IT',
      password: 'defaultPassword'
    });
    setEditMode(false);
    setCurrentStudentId(null);
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <button onClick={logout} className="logout-btn">Logout</button>
      </header>

      {error && <div className="alert error">{error}</div>}
      {success && <div className="alert success">{success}</div>}

      <section className="add-student-section">
        <h2>{editMode ? 'Edit Student' : 'Add Student'}</h2>
        <form onSubmit={handleSubmit} className="student-form">
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Course:</label>
            <select
              name="course"
              value={formData.course}
              onChange={handleInputChange}
              required
            >
              <option value="IT">IT</option>
              <option value="CS">Computer Science</option>
              <option value="Engineering">Engineering</option>
              <option value="Business">Business</option>
            </select>
          </div>
          {!editMode && (
            <div className="form-group">
              <label>Password:</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                required
              />
            </div>
          )}
          <div className="form-actions">
            <button type="submit">{editMode ? 'Update' : 'Add'} Student</button>
            {editMode && (
              <button type="button" onClick={cancelEdit}>Cancel</button>
            )}
          </div>
        </form>
      </section>

      <section className="students-list">
        <h2>Students</h2>
        {students.length === 0 ? (
          <p>No students found</p>
        ) : (
          <table className="students-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Course</th>
                <th>Enrollment Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {students.map(student => (
                <tr key={student._id}>
                  <td>{student.name}</td>
                  <td>{student.email}</td>
                  <td>{student.course}</td>
                  <td>{new Date(student.enrollmentDate).toLocaleDateString()}</td>
                  <td>
                    <button 
                      onClick={() => handleEdit(student)}
                      className="edit-btn"
                    >
                      Edit
                    </button>
                    <button 
                      onClick={() => handleDelete(student._id)}
                      className="delete-btn"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default AdminDashboard;
